var myAngular=angular.module("MyBlog",[]);myAngular.controller("sanCtrl",["$scope","$http",function(o,n){o.toggle_img=!0,o.getInfo=function(){n.get("/src/json/san.json").success(function(n){var t=window.location.href.substr(-1,1);o.info=n,o.item=o.info[t],o.toggle_img=!1,o.toggle_info=!0})},o.show_hide=function(){o.toggle_img=!0,o.toggle_info=!1},o.next=function(){var n=+o.item.id+1;n=n>7?n-8:n,o.item=o.info[n]},o.prev=function(){var n=+o.item.id-1;n=n<0?n+8:n,o.item=o.info[n]}}]);var test=angular.module("test",[]);test.controller("testctrl",["$scope",function(o){o.color="red",o.changeColor=function(){o.color="blue"}}]);