var myAngular=angular.module("MyBlog",[]),dom_coll={item:$(".grid-item"),grid:$(".grid"),san:$(".san-display"),bref:$(".bref-introduction")};myAngular.controller("sanCtrl",["$scope","$http",function(n,o){$(document).on("click",".grid-item",function(){var i=$(this).find("img").attr("data-id");o.get("/src/json/san.json").success(function(o){n.info=o,o.forEach(function(o){o.id==i&&(n.item=o)})}),dom_coll.grid.fadeOut(),dom_coll.san.fadeIn()}).on("click",".glyphicon-home",function(){dom_coll.grid.fadeIn(),dom_coll.san.fadeOut()})}]);